import React from 'react';
import Cell from './Cell';
import { GameState } from '../utils/gameTypes';
import './Board.css';

const BOARD_SIZE = 20;

interface BoardProps {
  game: GameState;
}

const Board: React.FC<BoardProps> = ({ game }) => {
  const agentX = game.agentPos.x;
  const agentY = game.agentPos.y;

  // Track which cells have ever been visible (fog removed)
  const [revealed, setRevealed] = React.useState(() =>
    Array.from({ length: BOARD_SIZE }, (_, y) => Array(BOARD_SIZE).fill(false))
  );

  React.useEffect(() => {
    setRevealed(prev => {
      const updated = prev.map(row => [...row]);
      for (let dy = -2; dy <= 2; dy++) {
        for (let dx = -2; dx <= 2; dx++) {
          const x = agentX + dx;
          const y = agentY + dy;
          if (x >= 0 && x < BOARD_SIZE && y >= 0 && y < BOARD_SIZE) {
            updated[y][x] = true;
          }
        }
      }
      return updated;
    });
  }, [agentX, agentY]);

  return (
    <div className="board-container" style={{ position: 'relative' }}>
      {/* Column numbers */}
      <div style={{ display: 'flex', flexDirection: 'row', marginLeft: 32 }}>
        <div style={{ width: 24 }} />
        {Array.from({ length: BOARD_SIZE }, (_, x) => (
          <div key={x} style={{ width: 40, textAlign: 'center', color: '#bbb', fontSize: 13 }}>{x + 1}</div>
        ))}
      </div>
      <div style={{ display: 'flex', flexDirection: 'row' }}>
        {/* Row numbers and grid */}
        <div style={{ display: 'flex', flexDirection: 'column' }}>
          {Array.from({ length: BOARD_SIZE }, (_, y) => (
            <div key={y} style={{ height: 40, width: 24, display: 'flex', alignItems: 'center', justifyContent: 'center', color: '#bbb', fontSize: 13 }}>{y + 1}</div>
          ))}
        </div>
        <div className="board">
          {game.board.map((row, y) =>
            row.map((cell, x) => {
              // A cell is revealed if it has ever been in the visible area
              const isRevealed = revealed[y][x];
              // A cell is explored (walked path) if marked as explored in game.explored
              const isExplored = cell.explored || game.explored[y][x];
              const fogged = !isRevealed;
              return (
                <Cell
                  key={`${x},${y}`}
                  type={cell.type}
                  explored={isExplored}
                  hasAgent={game.agentPos.x === x && game.agentPos.y === y}
                  fogged={fogged}
                />
              );
            })
          )}
        </div>
      </div>
    </div>
  );
};

export default Board;
